mapper.select:
select client_id, client_name, short_name, client_code, client_types, province, city,
county, detail_address, group_ids, group_names, post_code, tel_no, fax, remark, attachment_1,
attachment_2, invoice_title, registration_no, bank_name, bank_no, registration_address,
tel_no1, invoice_remark, bind_cpid, status, bind_company, link_man, link_duty, link_tel,
link_email, company_id
from t_client

  1=1

    and  company_id = #{companyId,jdbcType=BIGINT}


    and  client_typs = #{clientTypes,jdbcType=VARCHAR}


    and  group_ids in (#{groupIds,jdbcType=VARCHAR})


    and province = #{province,jdbcType=VARCHAR}


    and city = #{city,jdbcType=VARCHAR}


    and county = #{county,jdbcType=VARCHAR}


    and status = #{status,jdbcType=SMALLINT}


    and (client_name like '%#{complexStr,jdbcType=VARCHAR}%' or short_name like'%#{complexStr,jdbcType=VARCHAR}%' or client_code like '%#{complexStr,jdbcType=VARCHAR}%')


order by client_id

mapper.update:
update t_client
set status = #{status,jdbcType=SMALLINT}
where client_id = #{clientId,jdbcType=BIGINT}
