mapper.select:
select my_client_id, client_name, short_name, client_code, client_type, province,
city, county, detail_address, group_ids, group_names, post_code, tel_no, fax, remark,
attachment_1, attachment_2, invoice_title, registration_no, bank_name, bank_no, registration_address,
tel_no1, invoice_remark, status, company_id,bind_cpid,bind_company
from t_my_client t1

  1=1

    and  company_id = #{companyId,jdbcType=BIGINT}


    and  client_type = #{clientType,jdbcType=SMALLINT}


    and province = #{province,jdbcType=VARCHAR}


    and city = #{city,jdbcType=VARCHAR}


    and county = #{county,jdbcType=VARCHAR}


    and status = #{status,jdbcType=SMALLINT}


    and (client_name like '%#{complexStr,jdbcType=VARCHAR}%' or short_name like'%#{complexStr,jdbcType=VARCHAR}%' or client_code like '%#{complexStr,jdbcType=VARCHAR}%')


order by my_client_id

mapper.update:
update t_my_client
set status = #{status,jdbcType=SMALLINT}
where my_client_id = #{myClientId,jdbcType=BIGINT}
