mapper.select:
select order_id orderId, supplier, order_no orderNo, order_type orderType, contract_code contractCode, dispatching_type dispatchingType, pay_type payType, receive_warehouse receiveWarehouse, sender, sender_phone senderPhone, send_province sendProvince, send_city sendCity, send_district sendDistrict, send_address sendAddress, send_time sendTime, pack_require packRequire, receiver, receiver_phone receiverPhone, receiver_province receiverProvince, receiver_city receiverCity, receive_district receiveDistrict, receive_address receiveAddress, remarks, create_time createTime, group_id groupId,company_id companyId,receive_time receiveTime, approval_start_date approvalStartDate,approval_end_date approvalEndDate,create_user_id createUserId,order_serial_no orderSerialNo, approval_status approvalStatus,approval_process approvalProcess,supplier_id supplierId,is_draft isDraft,receive_require receiveRequire, summation,warehouse_id warehouseId,attachment1_name attachment1Name,attachment1,attachment2_name attachment2Name,attachment2, attachment3_name attachment3Name,attachment3,attachment4_name attachment4Name,attachment4,attachment5_name attachment5Name,attachment5 from t_order where (company_id = #{companyId,jdbcType=BIGINT} and order_type = #{orderType,jdbcType=SMALLINT}
and supplier like concat('%',#{supplier,jdbcType=VARCHAR},'%')
and supplier_id =#{supplierId,jdbcType=BIGINT}
and order_no like concat('%',#{orderNo,jdbcType=VARCHAR},'%')
and order_serial_no like concat('%',#{orderSerialNo,jdbcType=VARCHAR},'%')
and group_id=#{groupId,jdbcType=BIGINT}
and contract_code =#{contractCode,jdbcType=VARCHAR}
and dispatching_type = #{dispatchingType,jdbcType=SMALLINT}
and pay_type = #{payType,jdbcType=VARCHAR}
and create_time between concat(#{beginTime,jdbcType=TIMESTAMP},'000000') and concat(#{endTime,jdbcType=TIMESTAMP},'235959')
and create_time > concat(#{beginTime,jdbcType=TIMESTAMP},'000000')
and create_time < concat(#{endTime,jdbcType=TIMESTAMP},'235959')
and is_draft= #{isDraft,jdbcType=SMALLINT}
and order_id not in(select order_id from t_order where is_draft=1 and approval_status in(1,3,4))) order by create_time desc
