mapper.select:
select order_id, supplier, order_no, order_type, contract_code, dispatching_type, 
pay_type, receive_warehouse, file_url, sender, sender_phone, send_province, send_city, 
send_district, send_address, send_time, pack_require, receiver, receiver_phone, receiver_province, 
receiver_city, receive_district, receive_address, remarks, create_time, group_id,company_id,receive_time
from t_order

  
     supplier like concat('%',#{supplier,jdbcType=VARCHAR},'%')
  
  
	 and order_no =#{orderNo,jdbcType=VARCHAR}
  
  
  	and group_id=#{groupId,jdbcType=BIGINT}
  
  
  and contract_code =#{contractCode,jdbcType=VARCHAR}
  
  
  and dispatching_type = #{dispatchingType,jdbcType=SMALLINT}
  
  
  and pay_type = #{payType,jdbcType=VARCHAR}
  
  
     and create_time > #{beginTime,jdbcType=TIMESTAMP} and create_time < #{endTime,jdbcType=TIMESTAMP}
  
     and company_id = #{companyId,jdbcType=BIGINT} and order_type = #{orderType,jdbcType=SMALLINT}
