mapper.select:
select contract_id, contract_code, title, type, contract_status,
approval_status,approval_start_date, approval_end_date, group_id,company_id
from t_contract

  type = #{type,jdbcType=VARCHAR} and company_id = #{companyId,jdbcType=BIGINT}

    and title like concat('%',#{title,jdbcType=VARCHAR},'%') or contract_code like concat('%',#{title,jdbcType=VARCHAR},'%')


    and approval_start_date > #{approvalStartDate,jdbcType=TIMESTAMP} and approval_end_date < #{approvalEndDate,jdbcType=TIMESTAMP}




        and approval_status = 2


        and approval_status = #{approvalStatus,jdbcType=SMALLINT}
