mapper.select:
    select t1.contract_id, t1.contract_code, t1.title, t1.type, t1.contract_status,
    t1.approval_status,t1.approval_start_date, t1.approval_end_date, group_id,company_id,
    t2.ca_id, t2.user_id, t2.name, t2.dept_name, t2.sort, t2.type, t2.status, t2.content,t2.time
    from t_contract t1 left join
    (select * from t_contract_approval
    
      user_id = #{userId,jdbcType=BIGINT}
      
        and sort = 0
      
      
        and status = 1
      
      
        and status = 2
      
      
        and status = 0
      
    
    order by time) t2 on t1.contract_id = t2.contract_id
    
      t1.type = #{type,jdbcType=VARCHAR} and t1.company_id = #{companyId,jdbcType=BIGINT}
      
        and t1.title like concat('%',#{title,jdbcType=VARCHAR},'%') or t1.contract_code like concat('%',#{title,jdbcType=VARCHAR},'%')
      
      
        and t1.approval_start_date > #{approvalStartDate,jdbcType=TIMESTAMP} and t1.approval_end_date < #{approvalEndDate,jdbcType=TIMESTAMP}
      
      
        and t1.contract_status = #{contractStatus,jdbcType=SMALLINT}
      
      
        and t1.group_id = #{groupId,jdbcType=BIGINT}
      
      
        and t1.approval_status = #{approvalStatus,jdbcType=SMALLINT}
