mapper.select:
select contract_id, contract_code, serial_no, title, sign_date, type, contract_status,approval_status, approval_process, approval_process_id, cycle, pay_type, party_a_id, party_a_name, party_a_principal, party_b_id, party_b_name, party_b_principal, start_date, end_date,approval_start_date, approval_end_date, summary, terms, file_url, group_id,company_id, is_draft, summation, create_id, create_name, create_time, effective_time, termination_time, attachment1_name, attachment1, attachment2_name, attachment2, attachment3_name, attachment3, attachment4_name, attachment4, attachment5_name, attachment5 from t_contract
type = #{type,jdbcType=VARCHAR} and company_id = #{companyId,jdbcType=BIGINT}
and (title like concat('%',#{title,jdbcType=VARCHAR},'%') or serial_no like concat('%',#{title,jdbcType=VARCHAR},'%'))
and start_date >= #{beginTime,jdbcType=TIMESTAMP}
and #{endTime,jdbcType=TIMESTAMP} >= start_date
and contract_status = #{contractStatus,jdbcType=SMALLINT}
and group_id = #{groupId,jdbcType=BIGINT}
and approval_status = #{approvalStatus,jdbcType=SMALLINT}
and is_draft==0
order by
create_time
start_date
end_date
desc

mapper.update:
update t_contract
set contract_status = #{contractStatus,jdbcType=SMALLINT}

  ,start_date = #{startDate,jdbcType=TIMESTAMP}


  ,end_date = #{endDate,jdbcType=TIMESTAMP}


  ,is_draft = #{isDraft,jdbcType=SMALLINT}

where contract_id = #{contractId,jdbcType=BIGINT}
