mapper.select:
select contract_id, contract_code, title, sign_date, type, contract_status,approval_status, approval_process, cycle,
pay_type, party_a, party_a_principal, party_b, party_b_principal, start_date, end_date,approval_start_date, approval_end_date,
summary, terms, file_url, group_id,company_id
from t_contract

  type = #{type,jdbcType=VARCHAR} and company_id = #{companyId,jdbcType=BIGINT}

    and title like concat('%',#{title,jdbcType=VARCHAR},'%')


    and contract_code like concat('%',#{contractCode,jdbcType=VARCHAR},'%')


    and sign_date > #{signDate,jdbcType=TIMESTAMP} and sign_date < #{signDate,jdbcType=TIMESTAMP}


    and contract_status = #{contractStatus,jdbcType=SMALLINT}


    and group_id = #{groupId,jdbcType=BIGINT}


    and approval_status = #{approvalStatus,jdbcType=SMALLINT}

mapper.update:
update t_contract
set contract_status = #{contractStatus,jdbcType=SMALLINT}
where contract_id = #{contractId,jdbcType=BIGINT}
