mapper.select:
select t1.order_id, t1.order_no, t1.order_serial_no, t1.supplier, t1.summation, t1.approval_status, t1.approval_start_date, t1.approval_end_date from t_order t1 left join t_order_approval t2 on t1.order_id = t2.order_id
t1.order_type = #{orderType,jdbcType=VARCHAR} and t1.company_id = #{companyId,jdbcType=BIGINT}
and t1.order_serial_no like concat('%',#{orderSerialNo,jdbcType=VARCHAR},'%')
and t1.approval_start_date > #{approvalStartDate,jdbcType=TIMESTAMP} and t1.approval_start_date < #{approvalEndDate,jdbcType=TIMESTAMP}
and t1.approval_status = 2
and t1.approval_status = #{approvalStatus,jdbcType=SMALLINT}
and t1.approval_status != 0 and t1.is_draft = 1
and t2.user_id = #{userId,jdbcType=BIGINT} and t2.sort = 0
and t2.user_id = #{userId,jdbcType=BIGINT} and t2.status = 1
and t2.user_id = #{userId,jdbcType=BIGINT} and t2.status in(2,4,5) and t2.sort>0
and t2.user_id = #{userId,jdbcType=BIGINT} and t2.action_type = 1 and t1.approval_status=2
and t1.supplier_id = #{supplierId,jdbcType=BIGINT}
order by t1.order_id desc

select oa_id, user_id, user_name, dept_name, sort, result, status, action_type, content, time from t_order_approval

  and order_id = #{p1}
  

order by sort,oa_id
