mapper.update:

update t_customer_collection
set collection_name = #{collectionName,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}
where collection_id = #{collectionId,jdbcType=BIGINT} and company_id = #{companyId,jdbcType=BIGINT}

mapper.select:

select collection_id, collection_name, remark, create_id, create_name, create_date, company_id
from t_customer_collection
where  collection_id = #{0} and company_id = #{1}

select collection_id, collection_name,remark, create_id, create_name, create_date, company_id
from t_customer_collection

   company_id = #{companyId,jdbcType=BIGINT}

    and collection_name = #{collectionName,jdbcType=VARCHAR}


    and   collection_id != #{collectionId,jdbcType=BIGINT}


order by collection_id desc
