mapper.select:
contact_id, customer_id, name, duty, mobile, mail, birthday, gender, province, city, county, detail_address, remark, is_default, company_id from t_customer_contact
1=1
and company_id = #{companyId,jdbcType=BIGINT}
and customer_id = #{customerId,jdbcType=BIGINT}
order by contact_id

mapper.update:

update t_customer_contact
set  is_default = 0
where company_id = #{companyId,jdbcType=BIGINT}  && contact_id <> #{contactId,jdbcType=BIGINT}
