mapper.insert:

  SELECT LAST_INSERT_ID()

insert into event_meta (event_name, event_display_name, event_create_time
  )
values (#{eventName,jdbcType=VARCHAR}, #{eventDisplayName,jdbcType=VARCHAR}, #{eventCreateTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update event_meta
set event_name = #{eventName,jdbcType=VARCHAR},
  event_display_name = #{eventDisplayName,jdbcType=VARCHAR},
  event_create_time = #{eventCreateTime,jdbcType=TIMESTAMP}
where event_id = #{eventId,jdbcType=INTEGER}

mapper.select:
select event_id, event_name, event_display_name, event_create_time
from event_meta
where event_id = #{eventId,jdbcType=INTEGER}

 select event_id, event_name, event_display_name, event_create_time
from event_meta
WHERE event_name = #{eventName,jdbcType = VARCHAR}
