mapper.update:
update company_notify_setting
set company_id = #{companyId,jdbcType=BIGINT},
  notify_id = #{notifyId,jdbcType=BIGINT},
  enable_sms = #{enableSms,jdbcType=BIT},
  enable_web = #{enableWeb,jdbcType=BIT}
where setting_id = #{settingId,jdbcType=BIGINT}

mapper.select:
select setting_id, company_id, notify_id, enable_sms, enable_web
from company_notify_setting
where setting_id = #{settingId,jdbcType=BIGINT}
