mapper.update:
update notify
set receive_role = #{receiveRole,jdbcType=VARCHAR},
  template_id = #{templateId,jdbcType=INTEGER},
  notify_name = #{notifyName,jdbcType=VARCHAR},
  event_name = #{eventName,jdbcType=VARCHAR}
where notify_id = #{notifyId,jdbcType=BIGINT}

mapper.select:
select notify_id, receive_role, template_id, notify_name, event_name
from notify
where notify_id = #{notifyId,jdbcType=BIGINT}
