mapper.insert:

  SELECT LAST_INSERT_ID()

insert into valid_code_log (phone, last_send_time, send_count
  )
values (#{phone,jdbcType=VARCHAR}, #{lastSendTime,jdbcType=TIMESTAMP}, #{sendCount,jdbcType=INTEGER}
  )

mapper.update:
update valid_code_log
set phone = #{phone,jdbcType=VARCHAR},
  last_send_time = #{lastSendTime,jdbcType=TIMESTAMP},
  send_count = #{sendCount,jdbcType=INTEGER}
where valid_code_log_id = #{validCodeLogId,jdbcType=BIGINT}

mapper.select:
select valid_code_log_id, phone, last_send_time, send_count
from valid_code_log
where valid_code_log_id = #{validCodeLogId,jdbcType=BIGINT}

select valid_code_log_id, phone, last_send_time, send_count
from valid_code_log
WHERE phone = #{phone,jdbcType=VARCHAR}
