mapper.select:
  select b.*
  from balance_log b
  left join pay_order p on p.order_id = b.order_id
  where b.company_id = #{companyId}


    and b.log_type = #{orderType,jdbcType=INTEGER}



    and b.log_time >= #{beginTime,jdbcType=TIMESTAMP}



    and b.log_time <=  #{endTime,jdbcType=TIMESTAMP}


    and p.pay_type = #{payType,jdbcType=INTEGER}


order by log_time DESC
