mapper.select:
select balance_log_id, log_type, current_balance, amount, log_time, log_no, log_des,
log_username,company_id
from balance_log
where company_id = #{companyId}


  and order_type = #{log_type,jdbcType=INTEGER}



  and log_time >= #{beginTime,jdbcType=TIMESTAMP}



  and log_time <=  #{endTime,jdbcType=TIMESTAMP}
