  SELECT LAST_INSERT_ID()

insert into company_service_count (company_id, product_name, product_service_num
  )
values (#{companyId,jdbcType=BIGINT}, #{productName,jdbcType=VARCHAR}, #{productServiceNum,jdbcType=INTEGER}
  )

mapper.update:
update company_service_count
set company_id = #{companyId,jdbcType=BIGINT},
  product_name = #{productName,jdbcType=VARCHAR},
  product_service_num = #{productServiceNum,jdbcType=INTEGER}
where count_id = #{countId,jdbcType=INTEGER}

mapper.select:
select count_id, company_id, product_name, product_service_num
from company_service_count
where count_id = #{countId,jdbcType=INTEGER}

 select count_id, company_id, product_name, product_service_num
from company_service_count
where company_id = #{companyId,jdbcType=BIGINT}

  and product_name = #{productName,jdbcType=VARCHAR}
