mapper.insert:

  SELECT LAST_INSERT_ID()

insert into pay_order (order_type, order_amount, create_time, 
  order_pay_user_id, order_pay_company_id, order_status, 
  order_no,order_product_id,pay_type,create_user_name,order_des,balance)
values (#{orderType,jdbcType=INTEGER}, #{orderAmount,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{orderPayUserId,jdbcType=BIGINT}, #{orderPayCompanyId,jdbcType=BIGINT}, #{orderStatus,jdbcType=INTEGER}, 
  #{orderNo,jdbcType=VARCHAR},#{orderProductId,jdbcType=INTEGER},#{payType,jdbcType=INTEGER},#{createUserName,jdbcType=VARCHAR},#{orderDes,jdbcType=VARCHAR}
  ,#{balance,jdbcType=INTEGER}

)

mapper.select:
select

from pay_order

WHERE order_pay_company_id = #{companyId,jdbcType=BIGINT}

  and order_type = #{orderType,jdbcType=INTEGER}



  and create_time >= #{beginTime,jdbcType=TIMESTAMP}



  and create_time <=  #{endTime,jdbcType=TIMESTAMP}


  and pay_type = #{payType,jdbcType=INTEGER}



order by create_time DESC
