mapper.select:
  select order_id, order_type, order_amount, create_time, order_pay_user_id, order_pay_company_id,
order_status, order_no,order_product_id,pay_type
from pay_order

WHERE order_pay_company_id = #{companyId,jdbcType=BIGINT}

  and order_type = #{orderType,jdbcType=INTEGER}



  and create_time >= #{beginTime,jdbcType=DATETIME}



  and create_time <=  #{endTime,jdbcType=DATETIME}


  and pay_type = #{payType,jdbcType=INTEGER}



order by create_time DESC
