mapper.delete:
delete from t_admin_permission_relation
where 1=1

  and user_Id = #{userId,jdbcType=BIGINT}

mapper.update:
update t_admin_permission_relation
set user_id = #{userId,jdbcType=BIGINT},
  permission_id = #{permissionId,jdbcType=BIGINT}
where relation_id = #{relationId,jdbcType=BIGINT}

mapper.select:
select relation_id, user_id, permission_id
from t_admin_permission_relation
where relation_id = #{relationId,jdbcType=BIGINT}

select relation_id, user_id, permission_id
from t_admin_permission_relation
where user_id = #{userId,jdbcType=BIGINT}
