mapper.update:
update permission
set permission_remark = #{permissionRemark,jdbcType=VARCHAR},
  permission_name = #{permissionName,jdbcType=VARCHAR},
  permission_prefix = #{permissionPrefix,jdbcType=VARCHAR},
  permission_code = #{permissionCode,jdbcType=VARCHAR}
  ,permission_category = #{permissionCategory,jdbcType=VARCHAR}
where permission_id = #{permissionId,jdbcType=INTEGER}

mapper.select:
select permission_id, permission_remark, permission_name, permission_prefix, permission_code    ,permission_category
from permission
where permission_id = #{permissionId,jdbcType=INTEGER}
