mapper.select:
    
    select split_goods_id, waybill_plan_id, split_remark, create_id, create_name,
    create_date, update_id, update_name, update_time, is_deleted, carrier_company_id,company_id,cancel_remark,
    carrier_collection_ids,carrier_collection_names, carrier_phone, carrier_vehicle,carrier_company_name
    from t_split_goods
    
      
        and split_goods_id = #{splitGoodsId}
      
      
        and company_id = #{companyId,jdbcType=BIGINT}
      
      
        and  is_deleted = #{isDeleted,jdbcType=SMALLINT}
      
      
        and  carrier_company_id = #{carrierCompanyId,jdbcType=SMALLINT}
