    and audit_status = #{auditStatus,jdbcType=SMALLINT}


    and audit_date >= #{auditBeginTime,jdbcType=TIMESTAMP}


    and #{auditEndTime,jdbcType=TIMESTAMP} >= audit_date


    and account_code = #{accountCode,jdbcType=VARCHAR}


    and waybill_code = #{waybillCode,jdbcType=VARCHAR}


    and reconcile_code = #{reconcileCode,jdbcType=VARCHAR}


    and create_date >= #{createBeginTime,jdbcType=TIMESTAMP}


    and #{createEndTime,jdbcType=TIMESTAMP} >= create_date


order by account_id desc

