mapper.insert:

  SELECT LAST_INSERT_ID()

insert into t_fee_property (type, name, is_receivable, 
  is_show, company_id, operator_id, 
  operator_name, create_date, is_deleted
  )
values (#{type,jdbcType=SMALLINT}, #{name,jdbcType=VARCHAR}, #{isReceivable,jdbcType=SMALLINT}, 
  #{isShow,jdbcType=SMALLINT}, #{companyId,jdbcType=BIGINT}, #{operatorId,jdbcType=BIGINT}, 
  #{operatorName,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=SMALLINT}
  )

mapper.select:
select

from t_fee_property

  company_id = #{companyId,jdbcType=BIGINT}

    and is_deleted = #{isDeleted,jdbcType=SMALLINT}


    and type = #{type,jdbcType=SMALLINT}


    and is_receivable = #{isReceivable,jdbcType=SMALLINT}


    and name = #{name,jdbcType=VARCHAR}


    and pro_id != #{proId,jdbcType=BIGINT}


order by pro_id desc

mapper.update:
update t_fee_property
set is_deleted = 1
where pro_id = #{proId,jdbcType=BIGINT}
