mapper.select:
select sum(case when plan_status=30 then 1 else 0 end) as paidanzhong, sum(case when plan_status=15 then 1 else 0 end) as jingjiazhong, sum(case when send_card_status=1 then 1 else 0 end) as yipaidan, sum(case when plan_status=50 then 1 else 0 end) as yiwancheng from t_waybill_plan
company_id=#{companyId,jdbcType=BIGINT} and is_deleted=0
and group_id in
#{item,jdbcType=BIGINT}
and pubdate <= CURDATE() and pubdate >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and pubdate >= #{pubdate_start,jdbcType=TIMESTAMP} and pubdate <= #{pubdate_end,jdbcType=TIMESTAMP}

SELECT t.dt t_date,ifnull(t1.paidanzhong,0) paidanzhong,ifnull(t1.jingjiazhong,0) jingjiazhong,ifnull(t1.yipaidan,0) yipaidan, ifnull(t1.yiwancheng,0) yiwancheng FROM t_date t LEFT JOIN ( SELECT date(pubdate) as pub_date, sum(case when plan_status=30 then 1 else 0 end) as paidanzhong, sum(case when plan_status=15 then 1 else 0 end) as jingjiazhong, sum(case when send_card_status=1 then 1 else 0 end) as yipaidan, sum(case when plan_status=50 then 1 else 0 end) as yiwancheng FROM t_waybill_plan WHERE company_id =#{companyId,jdbcType=BIGINT} AND is_deleted = 0
and date(pubdate) <= CURDATE() and date(pubdate) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(pubdate) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(pubdate) >= #{pubdate_start,jdbcType=TIMESTAMP}
and group_id in
#{item,jdbcType=BIGINT}
group by DATE(pub_date) ) t1 on t.dt= t1.pub_date WHERE 1 = 1
and date(t.dt) <=CURDATE() and date(t.dt) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(t.dt) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(t.dt) >= #{pubdate_start,jdbcType=TIMESTAMP}

SELECT t.dt t_date,ifnull(t1.jingjiazhong,0) jingjiazhong,ifnull(t2.paichezhong,0) paichezhong, ifnull(t3.yipaiche,0) yipaiche,ifnull(t4.yiwancheng,0) yiwancheng,ifnull(t5.yibaojia,0) yibaojia FROM t_date t LEFT JOIN ( SELECT date(pubdate) as pub_date, count(1) jingjiazhong FROM t_waybill_plan wp WHERE is_deleted = 0
AND plan_status = '15' AND waybill_plan_id not in (SELECT waybill_plan_id FROM t_snatch_goods sn where plan_company_id = wp.company_id)
and date(pubdate) <= CURDATE() and date(pubdate) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(pubdate) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(pubdate) >= #{pubdate_start,jdbcType=TIMESTAMP}
and ${companyIds}
and ${carrierCollectionIds1}
group by DATE(pub_date) ) t1 on t.dt= t1.pub_date
LEFT JOIN ( SELECT date(wp.pubdate) as pub_date, count(1) paichezhong FROM t_split_goods sp LEFT JOIN t_waybill_plan wp on sp.waybill_plan_id = wp.waybill_plan_id WHERE wp.is_deleted = 0 and wp.plan_status!='60' and (select sum(remain_amount) from t_split_goods_detail spd where spd.split_goods_id=sp.split_goods_id)>0
and date(pubdate) <= CURDATE() and date(pubdate) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(pubdate) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(pubdate) >= #{pubdate_start,jdbcType=TIMESTAMP}
and ${companyIds}
and ${carrierCollectionIds2}
group by DATE(pub_date) ) t2 on t.dt= t2.pub_date
LEFT JOIN ( SELECT date(wp.pubdate) as pub_date, count(1) yipaiche FROM t_split_goods sp LEFT JOIN t_waybill_plan wp on sp.waybill_plan_id = wp.waybill_plan_id WHERE wp.is_deleted = 0 and wp.plan_status!='60' and (select sum(remain_amount) from t_split_goods_detail spd where spd.split_goods_id=sp.split_goods_id)=0
and date(pubdate) <= CURDATE() and date(pubdate) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(pubdate) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(pubdate) >= #{pubdate_start,jdbcType=TIMESTAMP}
and ${companyIds}
and ${carrierCollectionIds2}
group by DATE(pub_date) ) t3 on t.dt= t3.pub_date
LEFT JOIN ( SELECT date(wp.pubdate) as pub_date, count(1) yiwancheng FROM t_split_goods sp LEFT JOIN t_waybill_plan wp on sp.waybill_plan_id = wp.waybill_plan_id WHERE wp.is_deleted = 0
and date(pubdate) <= CURDATE() and date(pubdate) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(pubdate) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(pubdate) >= #{pubdate_start,jdbcType=TIMESTAMP}
and (select sum(remain_amount) from t_split_goods_detail spd where spd.split_goods_id=sp.split_goods_id)=0 and (SELECT count(1) from t_waybill wb where wb.split_goods_id=sp.split_goods_id and wb.company_id = sp.company_id and wb.is_deleted=0 AND wb.waybill_status!=7 )=0
and ${companyIds}
and ${carrierCollectionIds2}
group by DATE(pub_date) ) t4 on t.dt= t4.pub_date LEFT JOIN ( SELECT date(pubdate) as pub_date, count(1) yibaojia FROM t_waybill_plan wp WHERE 1=1 and waybill_plan_id in (SELECT waybill_plan_id FROM t_snatch_goods sn where sn.plan_company_id = wp.company_id and sn.is_deleted=0) and waybill_plan_id not in (SELECT waybill_plan_id FROM t_split_goods sg where sg.company_id = wp.company_id and sg.is_deleted=0) and plan_status != '60' and is_deleted = 0
and date(pubdate) <= CURDATE() and date(pubdate) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(pubdate) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(pubdate) >= #{pubdate_start,jdbcType=TIMESTAMP}
and ${companyIds}
and ${carrierCollectionIds1}
and ${companyIds}
group by DATE(pub_date) ) t5 on t.dt= t5.pub_date
WHERE 1 = 1
and date(t.dt) <=CURDATE() and date(t.dt) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(t.dt) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(t.dt) >= #{pubdate_start,jdbcType=TIMESTAMP}

select count(1) from t_waybill_plan wp where is_deleted=0 and (plan_status!='50' and plan_status!='60')

    and company_id != #{companyId}


    and ${companyIds}


    and ${carrierCollectionIds1}

select sum(case when (waybill_status!=7 and waybill_status!=8) then 1 else 0 end) as waybill_nums
from t_waybill where is_deleted=0 and company_id = #{companyId,jdbcType=BIGINT}

    and  ${groupIds}


    and  group_id is null

select sum(case when (waybill_status!=7 and waybill_status!=8) then 1 else 0 end) as waybill_nums
from t_waybill where is_deleted=0 and carrier_company_id = #{carrierCompanyId,jdbcType=BIGINT}

    and ${companyIds}


    and company_id is null
