mapper.select:
select count(own_vehicle_id)
from t_own_vehicle
where vehicle_num = #{0}

select own_vehicle_id, affiliated_company, vehicle_num, vehicle_trailer_num, vehicle_category, vehicle_type, vehicle_length, vehicle_load, vehicle_driver_phone, vehicle_driver_name, vehicle_brand, vehicle_specifications, vehicle_remark, vehicle_vin, vehicle_engine_number, chassis_number, vehicle_axle_number, vehicle_wheelbase, vehicle_tyre_amount, energy_type, swept_volume, vehicle_color, vehicle_factory_date, vehicle_buying_date, vehicle_annual_date, vehicle_two_level_period, vehicle_insurance_company, vehicle_policy_num, vehicle_insurance_expiry_date, vehicle_headerstock_photo, vehicle_tailstock_photo, vehicle_tyre_spec, group_id, create_id, create_name, create_date, update_id, update_name, update_date, is_deleted, compnay_id from t_own_vehicle
compnay_id = #{compnayId,jdbcType=BIGINT}
and affiliated_company like '%#{affiliatedCompany,jdbcType=VARCHAR}%'
and vehicle_num like '%#{vehicleNum,jdbcType=VARCHAR}%'
and (vehicle_driver_name like '%#{vehicleDriverName,jdbcType=VARCHAR}%' or vehicle_driver_phone like '%#{vehicleDriverName,jdbcType=VARCHAR}%')
and vehicle_category = #{vehicleCategory,jdbcType=VARCHAR}
