mapper.select:

select plan_leave_msg_id, waybill_plan_id, leave_type, leave_msg, leave_date, company_id,
company_name, shipper_name, carrier_company_id, carrier_company_name, carrier_name,
create_id, create_name, create_date, update_id, update_name, update_time, is_deleted
from t_plan_leave_msg


    and is_deleted = #{isDeleted,jdbcType=SMALLINT}


    and  waybill_plan_id = #{ waybillPlanId,jdbcType=BIGINT}


    and  company_id = #{ companyId,jdbcType=BIGINT}


    and  carrier_company_id = #{carrierCompanyId,jdbcType=BIGINT}



order by plan_leave_msg_id DESC
