mapper.select:
select reconcile_id reconcileId, reconcile_code reconcileCode, company_id companyId,transportation_expenses transportationExpenses, operator_id operatorId, operator_name operatorName, create_time createTime, cancel_ok cancelOk, account_id accountId, waybill_id waybillId, payee_type payeeType, payer_id payerId, payer_name payerName, group_id groupId, group_name groupName,other_expenses otherExpenses from t_fee_reconcile
cancel_ok=0 and company_id =#{companyId,jdbcType=BIGINT} and payee_type = #{payeeType,jdbcType=SMALLINT}
and create_time > concat(#{beginTime,jdbcType=VARCHAR},'000000')
and create_time < concat(#{endTime,jdbcType=VARCHAR},'235959')
and payer_name like concat('%',#{payerName,jdbcType=VARCHAR},'%')
and reconcile_code like concat('%',#{reconcileCode,jdbcType=VARCHAR},'%')
and group_id =#{groupId,jdbcType=BIGINT}
and operator_name = #{operatorName,jdbcType=VARCHAR}
order by create_time desc
