mapper.select:
select sum(case when plan_status=30 then 1 else 0 end) as paidanzhong, sum(case when plan_status=15 then 1 else 0 end) as jingjiazhong, sum(case when send_card_status=1 then 1 else 0 end) as yipaidan, sum(case when send_card_status=2 then 1 else 0 end) as yipaiche, sum(case when plan_status=50 then 1 else 0 end) as yiwancheng from t_waybill_plan
company_id=#{companyId,jdbcType=BIGINT} and is_deleted=0
and customer_name = #{customerName,jdbcType=VARCHAR}
and carrier_company_id=company_id
and carrier_company_id!=company_id
and group_id in
#{item,jdbcType=BIGINT}
and pubdate <= CURDATE() and pubdate >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and pubdate >= #{pubdate_start,jdbcType=TIMESTAMP} and pubdate <= #{pubdate_end,jdbcType=TIMESTAMP}

SELECT t.dt t_date,ifnull(t1.paidanzhong,0) paidanzhong,ifnull(t1.jingjiazhong,0) jingjiazhong,ifnull(t1.yipaidan,0) yipaidan, ifnull(t1.yipaiche,0) yipaiche,ifnull(t1.yiwancheng,0) yiwancheng FROM t_date t LEFT JOIN ( SELECT date(pubdate) as pub_date, sum(case when plan_status=30 then 1 else 0 end) as paidanzhong, sum(case when plan_status=15 then 1 else 0 end) as jingjiazhong, sum(case when send_card_status=1 then 1 else 0 end) as yipaidan, sum(case when send_card_status=2 then 1 else 0 end) as yipaiche, sum(case when plan_status=50 then 1 else 0 end) as yiwancheng FROM t_waybill_plan WHERE company_id =37 AND is_deleted = 0
and customer_name = #{customerName,jdbcType=VARCHAR}
and date(pubdate) <= CURDATE() and date(pubdate) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(pubdate) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(pubdate) >= #{pubdate_start,jdbcType=TIMESTAMP}
and group_id in
#{item,jdbcType=BIGINT}
and carrier_company_id=company_id
and carrier_company_id!=company_id
group by pub_date ) t1 on t.dt= t1.pub_date WHERE 1 = 1
and date(t.dt) <=CURDATE() and date(t.dt) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(t.dt) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(t.dt) >= #{pubdate_start,jdbcType=TIMESTAMP}

select sum(case when waybill_status=1 then 1 else 0 end) as daifahuo, sum(case when waybill_status=2 then 1 else 0 end) as yiruchang, sum(case when waybill_status=3 then 1 else 0 end) as yichuku, sum(case when waybill_status=4 then 1 else 0 end) as yunshuzhong, sum(case when waybill_status=5 then 1 else 0 end) as yixiehuo, sum(case when waybill_status=6 then 1 else 0 end) as yiqianshou, sum(case when waybill_status=7 then 1 else 0 end) as yiwancheng from t_waybill
company_id=#{companyId,jdbcType=BIGINT} and is_deleted=0
and customer_name = #{customerName,jdbcType=VARCHAR}
and carrier_company_id=company_id
and carrier_company_id!=company_id
and group_id in
#{item,jdbcType=BIGINT}
and pub_date <= CURDATE() and pub_date >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and pub_date >= #{pubdate_start,jdbcType=TIMESTAMP} and pub_date <= #{pubdate_end,jdbcType=TIMESTAMP}

SELECT t.dt t_date,ifnull(t1.daifahuo,0) daifahuo,ifnull(t1.yiruchang,0) yiruchang,ifnull(t1.yichuku,0) yichuku, ifnull(t1.yunshuzhong,0) yunshuzhong,ifnull(t1.yixiehuo,0) yixiehuo,ifnull(yiqianshou,0) yiqianshou,ifnull(yiwancheng,0) yiwancheng FROM t_date t LEFT JOIN ( SELECT date(pub_date) as pub_date, sum(case when waybill_status=1 then 1 else 0 end) as daifahuo, sum(case when waybill_status=2 then 1 else 0 end) as yiruchang, sum(case when waybill_status=3 then 1 else 0 end) as yichuku, sum(case when waybill_status=4 then 1 else 0 end) as yunshuzhong, sum(case when waybill_status=5 then 1 else 0 end) as yixiehuo, sum(case when waybill_status=6 then 1 else 0 end) as yiqianshou, sum(case when waybill_status=7 then 1 else 0 end) as yiwancheng FROM t_waybill WHERE company_id =37 AND is_deleted = 0
and customer_name = #{customerName,jdbcType=VARCHAR}
and date(pub_date) <= CURDATE() and date(pub_date) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(pub_date) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(pub_date) >= #{pubdate_start,jdbcType=TIMESTAMP}
and group_id in
#{item,jdbcType=BIGINT}
and carrier_company_id=company_id
and carrier_company_id!=company_id
group by pub_date ) t1 on t.dt= t1.pub_date WHERE 1 = 1
and date(dt) <=CURDATE() and date(dt) >= DATE_SUB(CURDATE(), INTERVAL #{date_interval,jdbcType=SMALLINT} DAY)
and date(dt) <= #{pubdate_end,jdbcType=TIMESTAMP} and date(dt) >= #{pubdate_start,jdbcType=TIMESTAMP}
