select id, waybill_plan_id,split_goods_id, waybill_code, waybill_status,waybill_source, group_id, group_name, driver_id,
driver_name, driver_phone, vechicle_id, vechicle_num, customer_id, customer_name,
customer_phone, sales_order, cust_contact_man, send_man, send_phone, send_province,
send_city, send_county, send_address, receive_man, receive_phone, receive_province,
receive_city, receive_county, receive_address, transport_way, goods_type, start_date,
arrive_date, is_receipt, is_urgent, pickup_way, distribution_way, attachment1, attachment1_name,
attachment2, attachment2_name, attachment3, attachment3_name, attachment4, attachment4_name,
attachment5, attachment5_name, waybill_remark, carrier_company_id,carrier_company_name, company_id, pub_date,
finish_date, cancel_date, cancel_man, cancel_remark, create_id, create_name, create_date,
update_id, update_name, update_date, is_deleted
from t_waybill
company_id = #{companyId,jdbcType=BIGINT}

    and waybill_code like concat('%',#{waybillCode,jdbcType=VARCHAR},'%')


    and waybill_status in (${waybillStatus})


    and waybill_plan_id = #{waybillPlanId,jdbcType=SMALLINT}


    and  ${groupIds}


    and  group_id is null


    and receive_province = #{receiveProvince,jdbcType=VARCHAR}


    and receive_city = #{receiveCity,jdbcType=VARCHAR}


    and receive_city = #{receiveCity,jdbcType=VARCHAR}


    and create_date >= #{startCreateDate,jdbcType=VARCHAR}


    and create_date <= #{endCreateDate,jdbcType=VARCHAR}


    and id in (select waybill_id from t_waybill_items where goods_name like concat('%',#{goodsName,jdbcType=VARCHAR},'%'))


    and (driver_name like concat('%',#{driverInfo,jdbcType=VARCHAR},'%') or driver_phone like concat('%',#{driverInfo,jdbcType=VARCHAR},'%') or vechicle_num like concat('%',#{driverInfo,jdbcType=VARCHAR},'%'))


    and start_date >= #{startStartDate,jdbcType=VARCHAR}


    and start_date <= #{endStartDate,jdbcType=VARCHAR}


    and is_deleted = #{isDeleted,jdbcType=VARCHAR}


order by id desc

select id, waybill_plan_id, split_goods_id,waybill_code, waybill_status,waybill_source, group_id, group_name, driver_id,
driver_name, driver_phone, vechicle_id, vechicle_num, customer_id, customer_name,
customer_phone, sales_order, cust_contact_man, send_man, send_phone, send_province,
send_city, send_county, send_address, receive_man, receive_phone, receive_province,
receive_city, receive_county, receive_address, transport_way, goods_type, start_date,
arrive_date, is_receipt, is_urgent, pickup_way, distribution_way, attachment1, attachment1_name,
attachment2, attachment2_name, attachment3, attachment3_name, attachment4, attachment4_name,
attachment5, attachment5_name, waybill_remark, carrier_company_id,carrier_company_name, company_id, pub_date,
finish_date, cancel_date, cancel_man, cancel_remark, create_id, create_name, create_date,
update_id, update_name, update_date, is_deleted
from t_waybill
carrier_company_id = #{carrierCompanyId,jdbcType=BIGINT}

    and ${companyIds}


    and company_id is null


    and waybill_code like concat('%',#{waybillCode,jdbcType=VARCHAR},'%')


    and waybill_status = #{waybillStatus,jdbcType=SMALLINT}


    and receive_province = #{receiveProvince,jdbcType=VARCHAR}


    and receive_city = #{receiveCity,jdbcType=VARCHAR}


    and receive_county = #{receiveCounty,jdbcType=VARCHAR}


    and create_date >= #{startCreateDate,jdbcType=VARCHAR}


    and create_date <= #{endCreateDate,jdbcType=VARCHAR}


    and waybill_source like '%'+#{waybillSource,jdbcType=VARCHAR}+'%'


    and id in (select waybill_id from t_waybill_items where goods_name like concat('%',#{goodsName,jdbcType=VARCHAR},'%'))


    and (driver_name like concat('%',#{driverInfo,jdbcType=VARCHAR},'%') or driver_phone like concat('%',#{driverInfo,jdbcType=VARCHAR},'%') or vechicle_num like concat('%',#{driverInfo,jdbcType=VARCHAR},'%'))


    and start_date >= #{startStartDate,jdbcType=VARCHAR}


    and start_date <= #{endStartDate,jdbcType=VARCHAR}


    and is_deleted = #{isDeleted,jdbcType=VARCHAR}


order by id desc

select id, waybill_plan_id,split_goods_id, waybill_code, waybill_status,waybill_source, group_id, group_name, driver_id, driver_name, driver_phone, vechicle_id, vechicle_num, customer_id, customer_name, customer_phone, sales_order, cust_contact_man, send_man, send_phone, send_province, send_city, send_county, send_address, receive_man, receive_phone, receive_province, receive_city, receive_county, receive_address, transport_way, goods_type, start_date, arrive_date, is_receipt, is_urgent, pickup_way, distribution_way, attachment1, attachment1_name, attachment2, attachment2_name, attachment3, attachment3_name, attachment4, attachment4_name, attachment5, attachment5_name, waybill_remark, carrier_company_id,carrier_company_name, company_id, pub_date, finish_date, cancel_date, cancel_man, cancel_remark, create_id, create_name, create_date, update_id, update_name, update_date, is_deleted from t_waybill company_id = #{companyId,jdbcType=BIGINT} and (waybill_status = 5 or waybill_status=7)
and waybill_plan_id = #{waybillPlanId,jdbcType=BIGINT}
and is_deleted = #{isDeleted,jdbcType=VARCHAR}
order by id desc

select id,w.driver_phone
from t_waybill w , t_waybill_position_setting p
 w.id=p.waybill_id and (w.waybill_status !=8 or w.waybill_status!=7) and w.is_deleted=0

    and find_in_set(#{fixedTime,jdbcType=VARCHAR},p.fixed_time)


    and fixed_interval = #{fixedInterval,jdbcType=INTEGER}


    and wps_type = #{wpsType,jdbcType=SMALLINT}


    and frequency_type = #{frequencyType,jdbcType=SMALLINT}


order by id desc

select id, waybill_code,company_id
from t_waybill
where company_id = #{companyId,jdbcType=BIGINT}
and  waybill_code= #{waybillCode,jdbcType=VARCHAR}
and is_deleted = 0
order by id desc

select , (select concat(fa.name,'、') from t_fee_account fa where fa.waybill_id=id and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) name, (select sum(ff.money) from t_fee_account fa, t_fee_flow ff where fa.waybill_id=id and ff.account_id=fa.account_id and ff.fee_property='运费' and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) freight_total, (select sum(ff.money) from t_fee_account fa, t_fee_flow ff where fa.waybill_id=id and ff.account_id=fa.account_id and ff.fee_property!='运费' and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) other_fee_total, (select sum(ff.money) from t_fee_account fa, t_fee_flow ff where fa.waybill_id=id and ff.account_id=fa.account_id and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) fee_total from t_waybill company_id = #{companyId,jdbcType=BIGINT}
and ${groupIds}
and group_id is null
order by id desc
