select , receivable_name receivAndPayName, payable_name receivAndPayName, (select sum(ff.money) from t_fee_account fa, t_fee_flow ff where fa.waybill_id=id and ff.account_id=fa.account_id and ff.fee_property='运费' and fa.is_deleted=0 and ff.is_deleted=0 and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) freight_total, (select sum(ff.money) from t_fee_account fa, t_fee_flow ff where fa.waybill_id=id and ff.account_id=fa.account_id and ff.fee_property!='运费' and fa.is_deleted=0 and ff.is_deleted=0 and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) other_fee_total, (select sum(ff.money) from t_fee_account fa, t_fee_flow ff where fa.waybill_id=id and ff.account_id=fa.account_id and fa.is_deleted=0 and ff.is_deleted=0 and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) fee_total, (select count(*) from t_fee_account fa where fa.waybill_id=id and fa.is_deleted=0 and fa.reconcile_id is not null and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) reconcile_count from t_waybill
waybill_status!=8 and is_deleted=0 and company_id = #{companyId,jdbcType=BIGINT}
and ${groupIds}
order by create_date desc

select , receivable_name receivAndPayName, payable_name receivAndPayName, (select sum(ff.money) from t_fee_account fa, t_fee_flow ff where fa.waybill_id=id and ff.account_id=fa.account_id and ff.fee_property='运费' and fa.is_deleted=0 and ff.is_deleted=0 and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) freight_total, (select sum(ff.money) from t_fee_account fa, t_fee_flow ff where fa.waybill_id=id and ff.account_id=fa.account_id and ff.fee_property!='运费' and fa.is_deleted=0 and ff.is_deleted=0 and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) other_fee_total, (select sum(ff.money) from t_fee_account fa, t_fee_flow ff where fa.waybill_id=id and ff.account_id=fa.account_id and fa.is_deleted=0 and ff.is_deleted=0 and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) fee_total, (select count(*) from t_fee_account fa where fa.waybill_id=id and fa.is_deleted=0 and fa.reconcile_id is not null and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT}) reconcile_count from t_waybill
