select sum(case when ff.fee_property='运费' then ff.money else 0 end) freight_total, sum(case when ff.fee_property!='运费' then ff.money else 0 end) other_fee_total, sum(ff.money) fee_total from t_waybill w,t_fee_account fa,t_fee_flow ff
fa.waybill_id=id and ff.account_id=fa.account_id and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT} and w.waybill_status!=8 and w.company_id = #{companyId,jdbcType=BIGINT} and fa.is_deleted=0 and ff.is_deleted=0 and w.is_deleted=0
and ${groupIds}

select sum(case when ff.fee_property='运费' then ff.money else 0 end) freight_total, sum(case when ff.fee_property!='运费' then ff.money else 0 end) other_fee_total, sum(ff.money) fee_total from t_waybill w,t_fee_account fa,t_fee_flow ff
fa.waybill_id=id and ff.account_id=fa.account_id and fa.is_receivable=#{isReceivable,jdbcType=SMALLINT} and w.waybill_status!=8 and w.carrier_company_id = #{carrierCompanyId,jdbcType=BIGINT} and fa.is_deleted=0 and ff.is_deleted=0 and w.is_deleted=0
and ${companyIds}
and w.company_id is null
and ${groupIds}
