mapper.select:
select id, waybill_id, transfer_date, vechicle_id, vechicle_num, driver_id, driver_name,
driver_phone, transfer_name, case_num, seal_num, company_id, create_id, create_name,
create_date, update_id, update_name, update_date, is_deleted
from t_waybill_transfer_record

  company_id = #{companyId,jdbcType=BIGINT}

    and waybill_id = #{waybillId,jdbcType=BIGINT}


    and is_deleted = #{isDeleted,jdbcType=SMALLINT}
