mapper.select:
select

from t_allot

  company_id = #{companyId,jdbcType=BIGINT}

    and is_deleted = #{isDeleted,jdbcType=SMALLINT}


    and allot_code like concat('%',#{allotCode,jdbcType=VARCHAR},'%')


    and allot_out_time >= CONCAT(STR_TO_DATE(#{allotOutBeginTime,jdbcType=VARCHAR},'%Y-%m-%d'),' ','00:00:00')


    and allot_out_time <= CONCAT(STR_TO_DATE(#{allotOutEndTime,jdbcType=VARCHAR},'%Y-%m-%d'),' ','23:59:59')


    and warehouse_out_id = #{warehouseOutId,jdbcType=BIGINT}


    and operator like concat('%',#{operator,jdbcType=VARCHAR},'%')


    and allot_in_time >= CONCAT(STR_TO_DATE(#{allotInBeginTime,jdbcType=VARCHAR},'%Y-%m-%d'),' ','00:00:00')


    and allot_in_time <= CONCAT(STR_TO_DATE(#{allotInEndTime,jdbcType=VARCHAR},'%Y-%m-%d'),' ','23:59:59')


    and warehouse_in_id = #{warehouseInId,jdbcType=BIGINT}


    and customer_name like concat('%',#{customerName,jdbcType=VARCHAR},'%')


    and group_id = #{groupId,jdbcType=BIGINT}


order by create_time desc
