mapper.select:
SELECT * from inventory_log a left join goods_info b on
a.goods_id = b.goods_id


        a.company_id = #{companyId}


        and a.inventory_id = #{inventoryId}



        and a.type = #{type}


        and a.customer_id = #{customerId}



        and a.warehouse_id = #{warehouseId}



        and a.storage_location_code like CONCAT(CONCAT('%',#{storageLocationCode}),'%')


        and a.log_time >=  #{since}


        and a.log_time <= #{until}


        and b.goods_name like CONCAT(CONCAT('%',#{goodsName}),'%')


        and b.goods_barcode like CONCAT(CONCAT('%',#{goodsbarcode}),'%')


        and a.business_no like CONCAT(CONCAT('%',#{businessNo}),'%')


order by log_time desc

select abs(sum(change_num)) num,time from (
select il.change_num,DATE_FORMAT(il.log_time,'%Y-%m-%d') time from inventory_log il
where il.log_time is not null
and il.company_id= #{companyId,jdbcType=BIGINT}

) a group by a.time

select a.innum,a.outnum,b.* from (select abs(sum(innum)) innum, abs(sum(outnum)) outnum,a.rowkey from ( select CASE when il.type= 0 then il.change_num else 0 end innum, CASE when il.type= 1 then il.change_num else 0 end outnum, CONCAT_WS('-',il.warehouse_id,il.customer_id,il.goods_id,il.batch)rowkey, il.type from inventory_log il join t_warehouse tw on il.warehouse_id = tw.wh_id join goods_info gi on il.goods_id = gi.goods_id where il.log_time is not null
and il.company_id= #{companyId,jdbcType=BIGINT}
) a group by a.rowkey)a
join ( select il.customer_id,il.customer_name ,il.warehouse_id,tw.wh_name, gi.goods_name,gi.goods_code,gi.goods_classify,il.batch,gi.min_unit,gi.goods_spec,gi.goods_barcode, CONCAT_WS('-',il.warehouse_id,il.customer_id,il.goods_id,il.batch)rowkey from inventory_log il join t_warehouse tw on il.warehouse_id = tw.wh_id join goods_info gi on il.goods_id = gi.goods_id where il.log_time is not null
and il.company_id= #{companyId,jdbcType=BIGINT}
group by il.customer_id,il.warehouse_id,gi.goods_code,il.batch )b on a.rowkey = b.rowkey order by b.goods_code
