mapper.select:
	 	select 
  
  	from shift_inventory_list
	
		company_id = #{companyId,jdbcType=BIGINT}
		
		and shift_inventory_num like concat('%',#{shiftInventoryNum,jdbcType=VARCHAR},'%')
		
		
		and group_id = #{groupId,jdbcType=BIGINT}
		
		
		and warehouse_id = #{warehouseId,jdbcType=BIGINT}
		
		
		and customer_id = #{customerId,jdbcType=BIGINT}
		
		
		and create_user = #{createUser,jdbcType=VARCHAR}
		
		
		and gmt_create > concat(#{beginTime,jdbcType=VARCHAR},'000000')
		
		
		and gmt_create < concat(#{endTime,jdbcType=VARCHAR},'235959')
		
		
		and finished = #{finished,jdbcType=TINYINT}
		
	
	    order by  gmt_create desc
