mapper.update:
update t_warehouse_loc
set is_deleted = 1
where wh_loc_id = #{whLocId,jdbcType=BIGINT}

mapper.select:
select wh_loc_id, wh_id, code, loc_type, name, status, remark, create_id,
create_name, create_date, update_id, update_name, update_time, is_deleted, company_id
from t_warehouse_loc

  company_id = #{companyId,jdbcType=BIGINT}

    and is_deleted = #{isDeleted,jdbcType=SMALLINT}


    and wh_id = #{whId,jdbcType=BIGINT}


    and loc_type = #{locType,jdbcType=SMALLINT}


    and code like concat('%',#{code,jdbcType=VARCHAR},'%')


order by wh_id desc

mapper.update:
update t_warehouse_loc
set is_deleted = 1
where wh_loc_id = #{whLocId,jdbcType=BIGINT}

mapper.select:
select count(*)
from t_warehouse_loc
where code = #{code,jdbcType=VARCHAR}
