mapper.update:

update t_goods_property_value
set goods_property_id = #{goodsPropertyId,jdbcType=BIGINT},
  property_name = #{propertyName,jdbcType=VARCHAR},
  value = #{value,jdbcType=VARCHAR},
  company_id = #{companyId,jdbcType=BIGINT}
where property_value_id = #{propertyValueId,jdbcType=BIGINT}

mapper.select:

select property_value_id, goods_property_id, property_name, value, company_id
from t_goods_property_value
where property_value_id = #{propertyValueId,jdbcType=BIGINT}


select property_value_id, goods_property_id, property_name, value, company_id
from t_goods_property_value
WHERE 1=1


    AND goods_property_id = #{goodsPropertyId,jdbcType=BIGINT}


    AND property_name = #{propertyName,jdbcType=VARCHAR}


    and value = #{value,jdbcType=VARCHAR}


    and company_id = #{companyId,jdbcType=BIGINT}


order by property_value_id
