mapper.insert:


  SELECT SCOPE_IDENTITY()

insert into t_goods_sku (goods_id, sku_code, sku_name, 
  sku_price, sku_spec, company_id
  )
values (#{goodsId,jdbcType=BIGINT}, #{skuCode,jdbcType=VARCHAR}, #{skuName,jdbcType=VARCHAR}, 
  #{skuPrice,jdbcType=DOUBLE}, #{skuSpec,jdbcType=VARCHAR}, #{companyId,jdbcType=BIGINT}
  )

mapper.select:

select goods_sku_id, goods_id, sku_code, sku_name, sku_price, sku_spec, company_id
from t_goods_sku
where goods_sku_id = #{goodsSkuId,jdbcType=BIGINT}


select goods_sku_id, goods_id, sku_code, sku_name, sku_price, sku_spec, company_id
from t_goods
WHERE 1=1


    AND sku_code = #{skuCode,jdbcType=VARCHAR}


    AND sku_name = #{skuName,jdbcType=VARCHAR}


    and goods_id = #{goodsId,jdbcType=BIGINT}


    and goods_sku_id = #{goodsSkuId,jdbcType=BIGINT}


    and company_id = #{companyId,jdbcType=BIGINT}


order by goods_sku_id
