mapper.update:
update t_custom_property
property_type = #{propertyType,jdbcType=SMALLINT}, property_name = #{propertyName,jdbcType=VARCHAR}, property_field = #{propertyField,jdbcType=VARCHAR}, company_id = #{companyId,jdbcType=BIGINT}, remarks = #{remarks,jdbcType=VARCHAR},
where custom_id = #{customId,jdbcType=BIGINT}

mapper.select:

select custom_id, property_type, property_name, property_field, company_id
from t_custom_property
where custom_id = #{customId,jdbcType=BIGINT}

select custom_id, property_type, property_name, property_field, company_id,remarks
from t_custom_property where company_id =#{companyId,jdbcType=BIGINT}
and property_type = #{propertyType,jdbcType=SMALLINT} and property_name=#{propertyName,jdbcType=VARCHAR}
