mapper.select:
select item_id, subject, code, bar_code, classify_name, classify_id, trade_type, purchase_price, wholesale_price, retail_price, introduction, images,conver_id, unit_id, unit_name, sub_item_property, store_rule, item_batch, combination_info, company_id, item_type, SELECT_PARENT_CLASSIFYID(#{classifyId,jdbcType=BIGINT}) as classify_ids from t_items_info
company_id = #{companyId,jdbcType=BIGINT}
and (subject like concat('%',#{subject,jdbcType=VARCHAR},'%') or item_id in (select DISTINCT item_id from t_sub_items_info where code like concat('%',#{code,jdbcType=VARCHAR},'%')) )
and item_type = #{itemType,jdbcType=SMALLINT}
and FIND_IN_SET(classify_id,SELECT_CHILDREN_CLASSIFYID(#{classifyId,jdbcType=BIGINT}))

select item_id, subject, code, bar_code, classify_name, classify_id, trade_type, purchase_price, wholesale_price, retail_price, introduction, images, conver_id, unit_id, unit_name, sub_item_property, store_rule, item_batch, combination_info, company_id, item_type from t_items_info
company_id = #{companyId,jdbcType=BIGINT}
and (subject like concat('%',#{subject,jdbcType=VARCHAR},'%') or item_id in (select DISTINCT item_id from t_sub_items_info where code like concat('%',#{code,jdbcType=VARCHAR},'%')) )
and item_type = #{itemType,jdbcType=SMALLINT}
and FIND_IN_SET(classify_id,SELECT_CHILDREN_CLASSIFYID(#{classifyId,jdbcType=BIGINT}))
order by item_id desc

select item_id, subject, code, bar_code, classify_name, classify_id, trade_type, purchase_price, wholesale_price, retail_price, introduction, images, conver_id, unit_id, unit_name, sub_item_property, store_rule, item_batch, combination_info, company_id, item_type from t_items_info
company_id = #{companyId,jdbcType=BIGINT}
and unit_id = #{unitId,jdbcType=BIGINT}
order by item_id desc
