mapper.update:
update t_admin_permission
set admin_permission_name = #{adminPermissionName,jdbcType=VARCHAR},
  admin_permission_code = #{adminPermissionCode,jdbcType=VARCHAR},
  admin_permission_category = #{adminPermissionCategory,jdbcType=VARCHAR}
where admin_permission_id = #{adminPermissionId,jdbcType=BIGINT}

mapper.select:
select admin_permission_id, admin_permission_name, admin_permission_code, admin_permission_category
from t_admin_permission
where admin_permission_id = #{adminPermissionId,jdbcType=BIGINT}
