mapper.select:
select comp_id, full_name, short_name, authentication, authentication_remark, industry, province, city, county, detail_address, post_code, tel_no, fax, comp_intro, link_man, link_duty, link_tel, link_email, link_remark, invoice_title, registration_no, bank_name, bank_no, registration_address, tel_no1, invoice_remark, company_logo,create_id,create_name,create_date from t_uc_company WHERE 1=1
and full_name = #{fullName,jdbcType=BIGINT}
order by comp_id
