mapper.select:

select member_id, user_id, company_id, company_name, reg_dt, depart_ids, role_ids,
positions, user_valid, login_dt, depart_ids_bak, depart_names, role_ids_bak, role_names
from front_member
WHERE 1=1


   AND member_id = #{memberId,jdbcType=BIGINT}


    and user_id = #{userId,jdbcType=BIGINT}


    and company_id = #{companyId,jdbcType=BIGINT}


    and company_name = #{companyName,jdbcType=VARCHAR}


order by member_id
