mapper.update:

update t_department
set dept_name = #{deptName,jdbcType=VARCHAR},
  dept_pid = #{deptPid,jdbcType=BIGINT},
  dept_order = #{deptOrder,jdbcType=BIGINT},
  company_id = #{companyId,jdbcType=BIGINT}
where dept_id = #{deptId,jdbcType=BIGINT}

mapper.select:

select dept_id, dept_name, dept_pid, dept_order, company_id,is_default
from t_department
where dept_id = #{deptId,jdbcType=BIGINT}


select dept_id, dept_name, dept_pid, dept_order, company_id,is_default
from t_department
where 1=1


    and company_id=#{companyId,jdbcType=BIGINT}


    and dept_name=#{deptName,jdbcType=VARCHAR}


    and dept_id <> #{deptId,jdbcType=BIGINT}


    and dept_pid = #{deptPid,jdbcType=BIGINT}


order by dept_order

select t1.dept_id,t1.dept_name,t1.dept_pid,t1.dept_order,t1.company_id, count(t2.dept_id) as child_num,t1.is_default from t_department t1 LEFT JOIN t_department t2 on t1.dept_id = t2.dept_pid where 1=1
and t1.company_id=#{companyId,jdbcType=BIGINT}
and t1.dept_pid = #{deptPid,jdbcType=BIGINT}
GROUP BY t1.dept_id,t1.dept_name,t1.dept_pid,t1.dept_order,t1.is_default,t1.company_id order by t1.dept_order
