from t_driver where driver_phone in

  #{item}

mapper.update:
update t_driver
set gps_status = #{gpsStatus,jdbcType=VARCHAR}
where driver_phone = #{driverPhone,jdbcType=VARCHAR}

mapper.select:
select t1.user_id, t1.affiliated_company, t1.driver_name, t1.driver_phone, t1.driver_ID, t1.driver_category,
t1.driver_email, t1.driver_sex, t1.driver_nationality, t1.driver_birthday, t1.driver_license, t1.driver_province,
t1.driver_city, t1.driver_district, t1.driver_address, t1.driver_header_img, t1.driver_license_photo,
t1.driver_ID_photo, t1.driver_ID_neg_photo, t1.driver_remark, t1.longitude, t1.latitude, t1.current_location,
t1.short_current_location, t1.gps_status,location_time,
t1.create_id, t1.create_name, t1.create_date, t1.update_id, t1.update_name, t1.update_date, t1.is_deleted,t1.auth_status
from t_driver t1 left join t_uc_user t2 on t1.user_id = t2.user_id


   and t1.driver_name = #{driverName}


    and t2.driver_phone = #{driverPhone}


    and t1.auth_status = #{authStatus}


    and t1.driver_province = #{driverProvince} and t1.driver_city = #{driverCity} and t1.driver_district = #{driverDistrict}


    and t1.create_date > createDateStart and t1.create_date < createDateEnd
