mapper.select:
select log_id, user_id, login_time, login_agent, login_ip, login_company_id
from login_log
where log_id = #{logId,jdbcType=BIGINT}

select log_id, u.user_id, login_time, login_agent, login_ip, login_company_id,
u.phone, u.real_name
from login_log l left JOIN
t_uc_user u on u.user_id = l.user_id
WHERE login_company_id = #{companyId,jdbcType=BIGINT}

  and u.real_name like concat('%',#{username,jdbcType=VARCHAR},'%')
  or u.phone like concat('%',#{username,jdbcType=VARCHAR},'%')


  and u.user_id = #{userId}


  and login_time >= #{beginTime,jdbcType=TIMESTAMP}


  and login_time <=  #{endTime,jdbcType=TIMESTAMP}


order by login_time DESC
