mapper.update:
update t_uc_user set mobile_country = #{mobileCountry,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, wechat = #{wechat,jdbcType=VARCHAR}, qq = #{qq,jdbcType=VARCHAR}, real_name = #{realName,jdbcType=VARCHAR}, nick_name = #{nickName,jdbcType=VARCHAR}, pwd = #{pwd,jdbcType=VARCHAR}, birthday = #{birthday,jdbcType=BIGINT}, picture_url = #{pictureUrl,jdbcType=VARCHAR}, intro_memo = #{introMemo,jdbcType=VARCHAR}, register_date = #{registerDate,jdbcType=TIMESTAMP}, referrer_phone = #{referrerPhone,jdbcType=VARCHAR}, user_status = #{userStatus,jdbcType=SMALLINT}
, last_login_time = #{lastLoginTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=BIGINT}

mapper.select:
SELECT user_id, mobile_country, phone, email, wechat, qq, real_name, nick_name, pwd,
birthday, picture_url, intro_memo, register_date, referrer_phone, user_status,register_from FROM t_uc_user
WHERE phone = #{phone,jdbcType=VARCHAR}

SELECT  u1.user_id, u1.mobile_country, phone, u1.email, u1.wechat, u1.qq, u1.real_name, u1.nick_name, (null) as pwd,
u1.birthday, u1.picture_url, u1.intro_memo, u1.register_date, u1.referrer_phone,  r1.is_enable as user_status FROM t_uc_user u1
left join t_uc_user_comp_rel r1 on r1.user_id=u1.user_id

     WHERE r1.user_id in(select g1.user_id from t_uc_user_group g1 where g1.company_id=#{companyId,jdbcType=BIGINT} and g1.group_id=#{groupId,jdbcType=BIGINT})
      AND  r1.comp_id = #{companyId,jdbcType=BIGINT}


      WHERE r1.user_id not in(select g1.user_id from t_uc_user_group g1 where g1.company_id=#{companyId,jdbcType=BIGINT} and g1.group_id=#{groupIdNot,jdbcType=BIGINT}) and r1.comp_id=#{companyId,jdbcType=BIGINT}
      AND r1.comp_id = #{companyId,jdbcType=BIGINT}

select  DISTINCT(u.user_id), mobile_country, u.phone, email, wechat, qq, real_name, nick_name,
   birthday, picture_url, intro_memo, register_date, referrer_phone, user_status,u.register_from,d.driver_name
 ,t1.admin_id,driver_phone,l.login_time
 from t_uc_user u
   left join t_driver d on d.user_id = u.user_id
   left join t_admin_user t1 on u.user_id = t1.admin_user_id
   LEFT JOIN (select max(login_time)  as login_time,user_id from login_log l  GROUP BY l.user_id order by login_time desc ) l ON l.user_id = u.user_id



        and phone = #{phone}


       and real_name like   concat(concat('%',#{realName}),'%')



       and (user_status = 1 or user_status is null)


       and user_status = #{userStatus}



       and register_from = #{registerFrom}



       and register_date >=  #{registerDateSince,jdbcType=TIMESTAMP}


       and register_date <=  #{registerDateUntil,jdbcType=TIMESTAMP}



       and l.login_time <=  #{loginDateUntil}


       and l.login_time >=  #{loginDateSince}


       and t1.admin_id is null


 order by u.register_date desc

select  u.user_id, mobile_country, phone, email, wechat, qq, real_name, nick_name,
birthday, picture_url, intro_memo, register_date, referrer_phone, user_status,register_from,d.driver_name,register_from
,t1.admin_id
from t_uc_user u

left join t_driver d on d.user_id = u.user_id
 join t_admin_user t1 on u.user_id = t1.admin_user_id


    phone = #{phone}


    and real_name like   concat(concat('%',#{realName}),'%')



    and user_status = #{userStatus}



    and register_from = #{registerFrom}



    and register_date >=  #{registerDateSince,jdbcType=TIMESTAMP}


    and register_date <=  #{registerDateUntil,jdbcType=TIMESTAMP}



    and l.login_time <=  #{loginDateUntil}


   and  l.login_time >=  #{loginDateSince}
