mapper.update:
update t_warehouse_linkman
set is_deleted = 1
where wh_linkman_id = #{whLinkmanId,jdbcType=BIGINT}

mapper.select:
select wh_linkman_id, wh_id, name, duty, mobile, mail, birthday, sex,
province, city, county, detail_address, remark, is_default, create_id, create_name, create_date,
company_id, update_id, update_name, update_time, is_deleted
from t_warehouse_linkman

  company_id = #{companyId,jdbcType=BIGINT}

    and  is_deleted = #{isDeleted,jdbcType=SMALLINT}


    and  wh_id = #{whId,jdbcType=BIGINT}


order by wh_linkman_id desc

mapper.update:
update t_warehouse_linkman
set is_default = #{isDefault,jdbcType=SMALLINT}
where wh_linkman_id = #{whLinkmanId,jdbcType=BIGINT}

update t_warehouse_linkman
set is_default = 0
where wh_id = #{whId,jdbcType=BIGINT}
